import React, { useState, useEffect, useRef } from 'react';
import LanguageSelector from '../components/LanguageSelector';

const inputLanguages = [
  { code: 'en-US', name: 'English' },
  { code: 'hi-IN', name: 'Hindi' },
  { code: 'bn-IN', name: 'Bengali' },
  { code: 'te-IN', name: 'Telugu' },
  { code: 'mr-IN', name: 'Marathi' },
  { code: 'ta-IN', name: 'Tamil' },
  { code: 'ur-IN', name: 'Urdu' },
  { code: 'gu-IN', name: 'Gujarati' },
  { code: 'kn-IN', name: 'Kannada' },
  { code: 'ml-IN', name: 'Malayalam' },
  { code: 'or-IN', name: 'Odia' },
  { code: 'pa-IN', name: 'Punjabi' },
  { code: 'as-IN', name: 'Assamese' },
  { code: 'mai-IN', name: 'Maithili' },
  { code: 'sat-IN', name: 'Santali' },
  { code: 'ks-IN', name: 'Kashmiri' },
  { code: 'ne-IN', name: 'Nepali' },
  { code: 'kok-IN', name: 'Konkani' },
  { code: 'sd-IN', name: 'Sindhi' },
  { code: 'doi-IN', name: 'Dogri' },
  { code: 'mni-IN', name: 'Manipuri' },
  { code: 'brx-IN', name: 'Bodo' },
];

const Home = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translation, setTranslation] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [recognition, setRecognition] = useState(null);
  const [error, setError] = useState(null);
  const transcriptRef = useRef('');
  const selectedLanguageRef = useRef(selectedLanguage);
  const [audioError, setAudioError] = useState('');
  const [inputLanguage, setInputLanguage] = useState('en-US');

  useEffect(() => {
    transcriptRef.current = transcript;
  }, [transcript]);

  useEffect(() => {
    selectedLanguageRef.current = selectedLanguage;
  }, [selectedLanguage]);

  useEffect(() => {
    // Initialize speech recognition
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = false; // Auto-stop after pause
      recognition.interimResults = true;
      recognition.lang = inputLanguage;

      recognition.onstart = () => {
        setError(null);
        setTranscript('');
        setTranslation('');
        transcriptRef.current = '';
      };

      recognition.onresult = (event) => {
        let transcript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          transcript += event.results[i][0].transcript;
        }
        setTranscript(transcript.trim());
        transcriptRef.current = transcript.trim();
      };

      recognition.onerror = (event) => {
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      recognition.onend = () => {
        setIsListening(false);
        // Use the ref to get the latest transcript and language
        if (transcriptRef.current.trim()) {
          translateText(transcriptRef.current.trim());
        }
      };

      setRecognition(recognition);
    } else {
      setError('Speech recognition is not supported in your browser. Please use Chrome.');
    }

    return () => {
      if (recognition) {
        recognition.stop();
      }
    };
    // eslint-disable-next-line
  }, [inputLanguage]);

  const playTranslation = () => {
    if (translation) {
      window.speechSynthesis.cancel(); // Cancel any ongoing speech
      const utterance = new window.SpeechSynthesisUtterance(translation);
      const voices = window.speechSynthesis.getVoices();
      const lang = selectedLanguageRef.current || 'en';
      let voice = voices.find(v => v.lang && v.lang.toLowerCase().startsWith(lang));
      if (!voice) {
        voice = voices.find(v => v.lang && v.lang.toLowerCase().includes(lang));
      }
      if (voice) {
        utterance.voice = voice;
        utterance.lang = lang;
        setAudioError('');
        window.speechSynthesis.speak(utterance);
      } else {
        setAudioError('Audio output for this language is not supported in your browser.');
      }
    }
  };

  const handleStartListening = () => {
    if (recognition) {
      try {
        recognition.start();
        setIsListening(true);
        setTranscript('');
        setTranslation('');
        setError(null);
        transcriptRef.current = '';
      } catch (err) {
        setError(`Error starting recognition: ${err.message}`);
        setIsListening(false);
      }
    }
  };

  const handleStopListening = () => {
    if (recognition) {
      try {
        recognition.stop();
        setIsListening(false);
        // No need to call translateText here; it will be called in onend
      } catch (err) {
        setError(`Error stopping recognition: ${err.message}`);
      }
    }
  };

  const translateText = async (text) => {
    try {
      const targetLang = selectedLanguageRef.current;
      console.log("Translating:", { text, target_lang: targetLang });
      const response = await fetch('http://localhost:8001/translate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text,
          target_lang: targetLang,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(
          errorData?.detail || `Translation failed with status: ${response.status}`
        );
      }

      const data = await response.json();
      setTranslation(data.translated_text);
      setError(null);
    } catch (error) {
      setError(`Translation error: ${error.message}`);
      setTranslation('');
    }
  };

  // Clear audio error when language or translation changes
  useEffect(() => {
    setAudioError('');
  }, [selectedLanguage, translation]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-blue-100 dark:from-gray-900 dark:to-gray-800 transition-colors duration-300">
      <div className="max-w-4xl mx-auto py-8 px-4">
        <h1 className="text-4xl font-extrabold text-center mb-4 text-gray-900 dark:text-white tracking-tight">
          VoiceBridge
        </h1>
        <p className="text-center text-lg text-gray-700 dark:text-gray-300 mb-8">
          <span className="font-semibold">Select the language you will speak in and the language you want to translate to.</span>
        </p>
        <div className="flex flex-col md:flex-row justify-center items-center gap-6 mb-8">
          <div className="flex flex-col items-center w-full md:w-1/2">
            <label htmlFor="input-language-select" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Select Input Language
            </label>
            <select
              id="input-language-select"
              value={inputLanguage}
              onChange={e => setInputLanguage(e.target.value)}
              className="block w-full max-w-xs rounded-lg border border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white py-2 px-3"
            >
              {inputLanguages.map(lang => (
                <option key={lang.code} value={lang.code}>{lang.name}</option>
              ))}
            </select>
          </div>
          <div className="flex flex-col items-center w-full md:w-1/2">
            <LanguageSelector
              selectedLanguage={selectedLanguage}
              onLanguageChange={(lang) => {
                console.log("Language changed to:", lang);
                setSelectedLanguage(lang);
              }}
            />
          </div>
        </div>
        <div className="flex flex-col items-center justify-center mb-8">
          <div className={`flex flex-col items-center bg-white dark:bg-gray-900 rounded-2xl shadow-xl p-8 w-full max-w-2xl border-2 ${isListening ? 'border-blue-500 dark:border-blue-400' : 'border-transparent'} transition-all duration-300`}>
            <div className="flex justify-center space-x-4 mb-4">
              <button
                onClick={handleStartListening}
                disabled={isListening}
                className={`flex items-center gap-2 px-6 py-2 rounded-lg text-lg font-semibold transition-colors duration-200 ${
                  isListening
                    ? 'bg-gray-300 dark:bg-gray-700 cursor-not-allowed text-gray-500'
                    : 'bg-blue-600 hover:bg-blue-700 text-white shadow-md'
                }`}
              >
                <span role="img" aria-label="microphone">🎤</span> Start Listening
              </button>
            </div>
            {isListening && (
              <p className="text-center mt-2 text-base text-blue-700 dark:text-blue-300 animate-pulse">
                Listening... <span className="font-semibold">(auto-stops when you pause)</span>
              </p>
            )}
            {error && (
              <p className="text-center mt-2 text-base text-red-600 dark:text-red-400">
                {error}
              </p>
            )}
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div className="bg-white dark:bg-gray-900 rounded-2xl shadow-lg p-6 border border-gray-200 dark:border-gray-700">
            <h2 className="text-2xl font-bold mb-4 text-gray-800 dark:text-white flex items-center gap-2">
              <span role="img" aria-label="wave">🗣️</span> Original Text
            </h2>
            <div className="min-h-[160px] p-4 bg-gray-50 dark:bg-gray-800 rounded-lg border border-gray-100 dark:border-gray-800">
              <p className="text-lg text-gray-800 dark:text-gray-200 whitespace-pre-line break-words">{transcript || 'No speech detected yet...'}</p>
            </div>
          </div>
          <div className="bg-white dark:bg-gray-900 rounded-2xl shadow-lg p-6 border border-gray-200 dark:border-gray-700">
            <h2 className="text-2xl font-bold mb-4 text-gray-800 dark:text-white flex items-center gap-2">
              <span role="img" aria-label="globe">🌐</span> Translation
            </h2>
            <div className="min-h-[160px] p-4 bg-gray-50 dark:bg-gray-800 rounded-lg border border-gray-100 dark:border-gray-800">
              <p className="text-lg text-gray-800 dark:text-gray-200 whitespace-pre-line break-words">{translation || 'No translation available yet...'}</p>
            </div>
            {['en', 'hi'].includes(selectedLanguageRef.current || selectedLanguage) && (
              <button
                onClick={playTranslation}
                disabled={!translation}
                className={`mt-4 px-6 py-2 rounded-lg text-lg font-semibold transition-colors duration-200 ${
                  translation
                    ? 'bg-green-500 hover:bg-green-600 text-white shadow-md'
                    : 'bg-gray-300 dark:bg-gray-700 cursor-not-allowed text-white'
                }`}
              >
                <span role="img" aria-label="speaker">🔊</span> Play Translation
              </button>
            )}
            {audioError && (
              <p className="mt-2 text-base text-red-600 dark:text-red-400">{audioError}</p>
            )}
          </div>
        </div>
        <footer className="mt-12 text-center text-xs text-gray-500 dark:text-gray-400">
          VoiceBridge &copy; {new Date().getFullYear()} &mdash; Powered by FastAPI, React, and Google Translate
        </footer>
      </div>
    </div>
  );
};

export default Home;
