import React, { useState, useEffect, useRef } from 'react';
import LanguageSelector from '../components/LanguageSelector';

const inputLanguages = [
  { code: 'en-US', name: 'English' },
  { code: 'hi-IN', name: 'Hindi' },
  { code: 'bn-IN', name: 'Bengali' },
  { code: 'te-IN', name: 'Telugu' },
  { code: 'mr-IN', name: 'Marathi' },
  { code: 'ta-IN', name: 'Tamil' },
  { code: 'ur-IN', name: 'Urdu' },
  { code: 'gu-IN', name: 'Gujarati' },
  { code: 'kn-IN', name: 'Kannada' },
  { code: 'ml-IN', name: 'Malayalam' },
  { code: 'or-IN', name: 'Odia' },
  { code: 'pa-IN', name: 'Punjabi' },
  { code: 'as-IN', name: 'Assamese' },
  { code: 'mai-IN', name: 'Maithili' },
  { code: 'sat-IN', name: 'Santali' },
  { code: 'ks-IN', name: 'Kashmiri' },
  { code: 'ne-IN', name: 'Nepali' },
  { code: 'kok-IN', name: 'Konkani' },
  { code: 'sd-IN', name: 'Sindhi' },
  { code: 'doi-IN', name: 'Dogri' },
  { code: 'mni-IN', name: 'Manipuri' },
  { code: 'brx-IN', name: 'Bodo' },
];

const Home = () => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [translation, setTranslation] = useState('');
  const [selectedLanguage, setSelectedLanguage] = useState('en');
  const [recognition, setRecognition] = useState(null);
  const [error, setError] = useState(null);
  const transcriptRef = useRef('');
  const selectedLanguageRef = useRef(selectedLanguage);
  const [audioError, setAudioError] = useState('');
  const [inputLanguage, setInputLanguage] = useState('en-US');

  useEffect(() => {
    transcriptRef.current = transcript;
  }, [transcript]);

  useEffect(() => {
    selectedLanguageRef.current = selectedLanguage;
  }, [selectedLanguage]);

  useEffect(() => {
    // Initialize speech recognition
    if ('webkitSpeechRecognition' in window) {
      const recognition = new window.webkitSpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = inputLanguage;

      recognition.onstart = () => {
        setError(null);
        setTranscript('');
        setTranslation('');
        transcriptRef.current = '';
      };

      recognition.onresult = (event) => {
        let transcript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          transcript += event.results[i][0].transcript;
        }
        setTranscript(transcript.trim());
        transcriptRef.current = transcript.trim();
      };

      recognition.onerror = (event) => {
        setError(`Speech recognition error: ${event.error}`);
        setIsListening(false);
      };

      recognition.onend = () => {
        setIsListening(false);
        // Use the ref to get the latest transcript and language
        if (transcriptRef.current.trim()) {
          translateText(transcriptRef.current.trim());
        }
      };

      setRecognition(recognition);
    } else {
      setError('Speech recognition is not supported in your browser. Please use Chrome.');
    }

    return () => {
      if (recognition) {
        recognition.stop();
      }
    };
    // eslint-disable-next-line
  }, [inputLanguage]);

  const playTranslation = () => {
    if (translation) {
      window.speechSynthesis.cancel(); // Cancel any ongoing speech
      const utterance = new window.SpeechSynthesisUtterance(translation);
      const voices = window.speechSynthesis.getVoices();
      const lang = selectedLanguageRef.current || 'en';
      let voice = voices.find(v => v.lang && v.lang.toLowerCase().startsWith(lang));
      if (!voice) {
        voice = voices.find(v => v.lang && v.lang.toLowerCase().includes(lang));
      }
      if (voice) {
        utterance.voice = voice;
        utterance.lang = lang;
        setAudioError('');
        window.speechSynthesis.speak(utterance);
      } else {
        setAudioError('Audio output for this language is not supported in your browser.');
      }
    }
  };

  const handleStartListening = () => {
    if (recognition) {
      try {
        recognition.start();
        setIsListening(true);
        setTranscript('');
        setTranslation('');
        setError(null);
        transcriptRef.current = '';
      } catch (err) {
        setError(`Error starting recognition: ${err.message}`);
        setIsListening(false);
      }
    }
  };

  const handleStopListening = () => {
    if (recognition) {
      try {
        recognition.stop();
        setIsListening(false);
        // No need to call translateText here; it will be called in onend
      } catch (err) {
        setError(`Error stopping recognition: ${err.message}`);
      }
    }
  };

  const translateText = async (text) => {
    try {
      const targetLang = selectedLanguageRef.current;
      console.log("Translating:", { text, target_lang: targetLang });
      const response = await fetch('http://localhost:8001/translate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text,
          target_lang: targetLang,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => null);
        throw new Error(
          errorData?.detail || `Translation failed with status: ${response.status}`
        );
      }

      const data = await response.json();
      setTranslation(data.translated_text);
      setError(null);
    } catch (error) {
      setError(`Translation error: ${error.message}`);
      setTranslation('');
    }
  };

  // Clear audio error when language or translation changes
  useEffect(() => {
    setAudioError('');
  }, [selectedLanguage, translation]);

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold text-center mb-8 text-gray-800 dark:text-white">
        VoiceBridge - Real-time Speech Recognition and Translation
      </h1>
      <p className="text-center text-base text-gray-600 dark:text-gray-300 mb-4">
        <span className="font-semibold">Select the language you will speak in and the language you want to translate to.</span>
      </p>
      <div className="flex flex-col md:flex-row justify-center items-center gap-4 mb-4">
        <div className="flex flex-col items-center">
          <label htmlFor="input-language-select" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            Select Input Language
          </label>
          <select
            id="input-language-select"
            value={inputLanguage}
            onChange={e => setInputLanguage(e.target.value)}
            className="block w-full max-w-xs rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
          >
            {inputLanguages.map(lang => (
              <option key={lang.code} value={lang.code}>{lang.name}</option>
            ))}
          </select>
        </div>
        <LanguageSelector
          selectedLanguage={selectedLanguage}
          onLanguageChange={(lang) => {
            console.log("Language changed to:", lang);
            setSelectedLanguage(lang);
          }}
        />
      </div>

      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 mb-8">
        <div className="mt-6">
          <div className="flex justify-center space-x-4">
            <button
              onClick={handleStartListening}
              disabled={isListening}
              className={`px-4 py-2 rounded-lg ${
                isListening
                  ? 'bg-gray-300 dark:bg-gray-600 cursor-not-allowed'
                  : 'bg-blue-500 hover:bg-blue-600 text-white'
              }`}
            >
              Start Listening
            </button>
            <button
              onClick={handleStopListening}
              disabled={!isListening}
              className={`px-4 py-2 rounded-lg ${
                !isListening
                  ? 'bg-gray-300 dark:bg-gray-600 cursor-not-allowed'
                  : 'bg-red-500 hover:bg-red-600 text-white'
              }`}
            >
              Stop Listening
            </button>
          </div>
          {isListening && (
            <p className="text-center mt-4 text-sm text-gray-600 dark:text-gray-400">
              Listening... Speak now
            </p>
          )}
          {error && (
            <p className="text-center mt-4 text-sm text-red-600 dark:text-red-400">
              {error}
            </p>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">
            Original Text
          </h2>
          <div className="min-h-[200px] p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
            <p className="text-gray-800 dark:text-gray-200">{transcript || 'No speech detected yet...'}</p>
          </div>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white">
            Translation
          </h2>
          <div className="min-h-[200px] p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
            <p className="text-gray-800 dark:text-gray-200">{translation || 'No translation available yet...'}</p>
          </div>
          <button
            onClick={playTranslation}
            disabled={!translation}
            className={`mt-4 px-4 py-2 rounded-lg ${
              translation
                ? 'bg-green-500 hover:bg-green-600 text-white'
                : 'bg-gray-300 dark:bg-gray-600 cursor-not-allowed text-white'
            }`}
          >
            Play Translation
          </button>
          {audioError && (
            <p className="mt-2 text-sm text-red-600 dark:text-red-400">{audioError}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
